

namespace Business.Models;

/// <summary>
/// Indicates whether the operation was successful or not and sends back a message and any data.
/// </summary>
/// <typeparam name="T">Represents the type of object</typeparam>
public class Result<T>
{
    public bool IsSuccess { get; set; }
    public string Message { get; set; } = string.Empty;
    public T? Data { get; set; }


//Added contructors and factory methods generated by ChatGPT4o to simplify usage of the class.
    public Result() { }

    public Result(bool isSuccess, string message, T? data = default)
    {
        IsSuccess = isSuccess;
        Message = message;
        Data = data;
    }

    public static Result<T> Success(T data, string message = "") =>
        new Result<T> { IsSuccess = true, Message = message, Data = data };

    public static Result<T> EmptySuccess(string message = "") =>
        new Result<T> { IsSuccess = true, Message = message };

    public static Result<T> Failure(string message = "") =>
        new Result<T> { IsSuccess = false, Message = message };
}
